<script>
    $().ready(function() {
        let event_info = $('#event-info')[0];
        let property_name = $('#property-name')[0];
        let partenaire_icon = $('#partenaire-icon');
        let ID = $('#ID')[0];
        let guest_picture = $('#guest-picture');
        let guest_name = $('#guest-name')[0];
        let guest_email = $('#guest-email');
        let arrival_date = $('#arrival-date')[0];
        let arrival_time = $('#arrival-time')[0];
        let departure_date = $('#departure-date')[0];
        let departure_time = $('#departure-time')[0];
        let prices = $('#prices')[0];
        let adults = $('#adults')[0];
        let nights = $('#nights')[0];
        let children = $('#children')[0];
        let animals = $('#animals')[0];
        let guest_phone = $('#guest-phone')[0];

        // Initialize the intl-tel-input library
        const phoneInput = intlTelInput(guest_phone, {
            // Important for identifying the country from the phone number
            utilsScript: "https://cdn.jsdelivr.net/npm/intl-tel-input@17/build/js/utils.js",
            // Disable formatting since you want to show the exact phone number from DB
            formatOnDisplay: false,
            // Set initial country based on the number itself
            initialCountry: "auto",
        });


        // Initial Date
        let date = new Date();
        date.setDate(date.getDate());

        let calendar = new Calendar($('#calendar')[0], {
            plugins: [resourceTimelinePlugin],
            slotMinWidth: 65,
            initialDate: date,
            // defaultDate: '2024-07-30', // Set the default selected date to July 30, 2024
            firstDay: 1,
            contentHeight: "auto",
            slotLabelFormat: [{
                day: '2-digit',
                month: 'short',
            }, ],
            slotLabelContent: function(arg) {
                var date = arg.date;
                var day = date.toLocaleString('default', {
                    day: '2-digit'
                });
                var month = date.toLocaleString('default', {
                    month: 'short'
                });

                return {
                    html: day + '<br/>' + month
                };
            },
            // nowIndicator: true,
            // initialDate: date.toISOString().split('T')[0], 
            headerToolbar: {
                left: "",
                center: "",
                right: ""
            },

            views: { // Modifier les vues
                resourceTimelineTwoWeek: {
                    type: 'resourceTimeline',
                    duration: {
                        days: 30,
                    },
                },
            },
            slotDuration: {
                hours: 24
            },
            editable: false, //Drag & Drop
            // selectable: true, 
            // aspectRatio: 5,
            resourceAreaColumns: [{
                headerContent: 'PROPERTIES'
            }, ],
            initialView: 'resourceTimelineTwoWeek',
            resources: @json($resources),
            events: @json($events),

            eventContent: function(arg) {
                const defaultPic = "./storage/avatars/default.jpg";
                var guest_name = arg.event.title;
                var icon = arg.event.extendedProps.icon;
                var picture = arg.event.extendedProps.picture;
                picture = picture ? picture : defaultPic;
                return {
                    html: `
               <div class="fc-content overflow-hidden ${icon.name.toLowerCase()}">
                  <div style="width: 100%;" class="fc-title h-11 flex justify-start items-center px-2">
                    <div class="flex items-center">
                      <img class="w-7 h-7 rounded-full" src="${picture}" alt="" srcset="">
                      <p class="ps-3 text-xs font-light text-gray-900">${guest_name}</p>
                    </div>
                    <div>
                      <img class="w-6 h-6 rounded-full" src="${icon.url}" alt="${icon.name}" srcset="">
                    </div>
                  </div>
                </div>
                `
                };
            },
            eventClick: function(info) {
                let event = info.event;
                property_name.textContent = event.getResources()[0].title;
                ID.textContent = event.getResources()[0].id;
                partenaire_icon.attr('src', event.extendedProps.icon.url);
                guest_picture.attr('src', event.extendedProps.picture);
                guest_name.textContent = event.title;
                guest_email.attr('placeholder', event.extendedProps.email);
                nights.textContent = event.extendedProps.nights;
                children.textContent = event.extendedProps.children;
                animals.textContent = event.extendedProps.animals;
                adults.textContent = event.extendedProps.adults;
                prices.textContent = event.extendedProps.prices;
                arrival_date.textContent = event.extendedProps.arrival;
                arrival_time.textContent = event.extendedProps.arrivalTime;
                departure_date.textContent = event.extendedProps.departure;
                departure_time.textContent = event.extendedProps.departureTime;

                // After the plugin is initialized, set the number and flag
                phoneInput.setNumber(event.extendedProps.phone);

                // Manually set the input value to the phone number without formatting it
                // guest_phone.value = event.extendedProps.phone;

                // Example: Set a phone number from the database (replace with actual data)
                // const phoneNumber = "+14155552671"; // Example number
                // iti.setNumber(phoneNumber);

                // Fetching countries json file 
                // fetch(
                //     ) // Adjust the path according to where the file is
                //     .then(response => {
                //         if (!response.ok) {
                //             throw new Error(`HTTP error! Status: ${response.status}`);
                //         }
                //         return response.json();
                //     })
                //     .then(countries => {
                //         // searsh for the country_code
                //         countries.forEach(country => {
                //             if (country.code === event.extendedProps
                //                 .phone_code) {

                //                 $('#phone-flag')[0].innerHTML =
                //                     `<span class="me-1 self-center fi fi-${country.iso.toLowerCase()}"></span><span style="color: #666565;" class="self-center">+${country.code}</span>`;
                //             }
                //         });
                //     })
                //     .catch(error => {
                //         console.error('Error:', error);
                //     });

                // // countries.forEach(country => {
                // //     if (country.phone_code === event.extendedProps
                // //         .contry_code) {
                // //         $('#phone-flag')[0].innerHTML = country.flag_svg +
                // //             country
                // //             .phone_code;
                // //         // $('#country').val(country.id);
                // //     }
                // // });


                event_info.classList.remove('hidden');
            }
        }); // end calendar

        calendar.render();


        // Listening for click to close event _infos
        $('#close-button').click(function() {
            event_info.classList.add('hidden');
        });

        // ----- change border color for event according to partenaire
        Object.entries(@json($borderColors)).forEach(([name, borderColor]) => {
            $(`.${name}`).each(function() {
                $(this).parent().parent().css({
                    'border-top': `5px solid ${borderColor}`
                });
            });
        }); // end border change 
    }); // end ready funct 


    function formatPhoneNumber(phoneNumber) {
        // Remove any existing spaces
        phoneNumber = phoneNumber.replace(/\s+/g, '');

        // Add a space every three digits
        return phoneNumber.replace(/(\d{3})(?=\d)/g, '$1 ');
    }
</script>
